"use client";

import React, { useEffect, useState } from 'react';
import { Table, Button, Modal, Form, Input, Select, message, Tag, Space, Popconfirm } from 'antd';
import { PlusOutlined, DeleteOutlined, EditOutlined } from '@ant-design/icons';
import api from '@/lib/axios';

export default function UsersPage() {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(false);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingUser, setEditingUser] = useState<any>(null);
    const [form] = Form.useForm();

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        setLoading(true);
        try {
            const { data } = await api.get('/users');
            setUsers(data);
        } catch (e) {
            console.error(e);
            message.error('Failed to load users');
        } finally {
            setLoading(false);
        }
    };

    const handleSubmit = async (values: any) => {
        try {
            if (editingUser) {
                // Update
                await api.patch(`/users/${editingUser.id}`, values);
                message.success('User updated');
            } else {
                // Create
                await api.post('/users', values);
                message.success('User created');
            }
            setIsModalOpen(false);
            form.resetFields();
            setEditingUser(null);
            fetchUsers();
        } catch (e: any) {
            console.error(e);
            message.error(e.response?.data?.message || 'Operation failed');
        }
    };

    const handleDelete = async (id: number) => {
        try {
            await api.delete(`/users/${id}`);
            message.success('User deleted');
            fetchUsers();
        } catch (e) {
            message.error('Failed to delete');
        }
    };

    const columns = [
        { title: 'ID', dataIndex: 'id', key: 'id' },
        { title: 'Username', dataIndex: 'username', key: 'username' },
        { title: 'Full Name', dataIndex: 'fullName', key: 'fullName' },
        {
            title: 'Role',
            dataIndex: 'role',
            key: 'role',
            render: (role: string) => <Tag color="blue">{role}</Tag>
        },
        { title: 'Branch ID', dataIndex: 'branchId', key: 'branchId' },
        {
            title: 'Actions',
            key: 'actions',
            render: (_: any, record: any) => (
                <Space>
                    <Button
                        icon={<EditOutlined />}
                        onClick={() => {
                            setEditingUser(record);
                            form.setFieldsValue(record);
                            setIsModalOpen(true);
                        }}
                    />
                    <Popconfirm title="Delete user?" onConfirm={() => handleDelete(record.id)}>
                        <Button danger icon={<DeleteOutlined />} />
                    </Popconfirm>
                </Space>
            )
        }
    ];

    return (
        <div>
            <div className="flex justify-between items-center mb-6">
                <h2 className="text-2xl font-bold">User Management</h2>
                <Button
                    type="primary"
                    icon={<PlusOutlined />}
                    onClick={() => {
                        setEditingUser(null);
                        form.resetFields();
                        setIsModalOpen(true);
                    }}
                    style={{ backgroundColor: '#E60000' }}
                >
                    Add User
                </Button>
            </div>

            <Table dataSource={users} columns={columns} rowKey="id" loading={loading} />

            <Modal
                title={editingUser ? "Edit User" : "Create User"}
                open={isModalOpen}
                onCancel={() => setIsModalOpen(false)}
                footer={null}
            >
                <Form form={form} onFinish={handleSubmit} layout="vertical">
                    <Form.Item name="username" label="Username" rules={[{ required: true }]}>
                        <Input readOnly={!!editingUser} />
                    </Form.Item>
                    <Form.Item name="fullName" label="Full Name" rules={[{ required: true }]}>
                        <Input />
                    </Form.Item>
                    <Form.Item
                        name="password"
                        label={editingUser ? "New Password (Leave blank to keep current)" : "Password"}
                        rules={[{ required: !editingUser }]}
                    >
                        <Input.Password placeholder={editingUser ? "Enter new password if you want to change it" : ""} />
                    </Form.Item>
                    <Form.Item name="role" label="Role" rules={[{ required: true }]}>
                        <Select>
                            <Select.Option value="SUPER_ADMIN">Super Admin</Select.Option>
                            <Select.Option value="HQ_ADMIN">HQ Admin</Select.Option>
                            <Select.Option value="BRANCH_ADMIN">Branch Admin</Select.Option>
                            <Select.Option value="TECHNICIAN">Technician</Select.Option>
                            <Select.Option value="COURIER">Courier</Select.Option>
                        </Select>
                    </Form.Item>
                    <Form.Item name="branchId" label="Branch ID (Optional)">
                        <Input type="number" />
                    </Form.Item>
                    <Button type="primary" htmlType="submit" block>
                        {editingUser ? 'Update' : 'Create'}
                    </Button>
                </Form>
            </Modal>
        </div>
    );
}

"use client";

import React, { useState, useEffect } from 'react';
import { Form, Input, Button, Card, Select, Typography, Row, Col, Divider, Statistic, App } from 'antd';
import { MedicineBoxOutlined, ToolOutlined, SaveOutlined } from '@ant-design/icons';
import api from '@/lib/axios';
import { useRouter } from 'next/navigation';

const { Title, Text } = Typography;
const { Option } = Select;

export default function DiagnosticPage() {
    const [form] = Form.useForm();
    const router = useRouter();
    const { message } = App.useApp();
    const [loading, setLoading] = useState(false);
    const [estimatedPrice, setEstimatedPrice] = useState<number | null>(null);

    // Watch for changes to model/issue to update price
    const model = Form.useWatch('deviceModel', form);
    const issue = Form.useWatch('issueType', form);

    useEffect(() => {
        if (model && issue) {
            fetchPrice(model, issue);
        } else {
            setEstimatedPrice(null);
        }
    }, [model, issue]);

    const fetchPrice = async (model: string, issue: string) => {
        try {
            // In real app: const { data } = await api.get(`/prices?model=${model}&issue=${issue}`);
            // Mocking logic here for demonstration if backend not ready, 
            // but we should use the axios mock we set up.
            const { data } = await api.get('/prices', { params: { model, issue } });
            if (data && data.price) {
                setEstimatedPrice(data.price);
            }
        } catch (error) {
            console.error("Failed to fetch price", error);
        }
    };

    const handleSaveLead = async (values: any) => {
        setLoading(true);
        try {
            await api.post('/crm/leads', { ...values, status: 'Declined' });
            message.info('Lead saved (Customer declined repair).');
            form.resetFields();
            setEstimatedPrice(null);
        } catch (error) {
            message.error('Failed to save lead.');
        } finally {
            setLoading(false);
        }
    };

    const handleProceed = async () => {
        try {
            const values = await form.validateFields();
            setLoading(true);

            // 1. Save to CRM first
            await api.post('/crm/leads', { ...values, status: 'Proceeding' });

            // 2. Pass data to Create Repair page via Session Storage
            sessionStorage.setItem('diagnostic_data', JSON.stringify(values));

            message.success('Proceeding to repair creation...');
            router.push('/repairs/create');
        } catch (error) {
            console.error(error);
            // If validation fails, do nothing
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="max-w-4xl mx-auto">
            <div className="mb-6 flex items-center">
                <Title level={2} className="m-0">Diagnostic & Triage</Title>
            </div>

            <Row gutter={24}>
                <Col xs={24} lg={16}>
                    <Card title="Customer & Device Information" className="shadow-sm rounded-xl">
                        <Form
                            form={form}
                            layout="vertical"
                            size="large"
                        >
                            <Divider>Customer Details</Divider>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item name="customerName" label="Full Name" rules={[{ required: true }]}>
                                        <Input placeholder="John Doe" />
                                    </Form.Item>
                                </Col>
                                <Col span={12}>
                                    <Form.Item name="customerContact" label="Contact Number" rules={[{ required: true }]}>
                                        <Input placeholder="+1 234 567 890" />
                                    </Form.Item>
                                </Col>
                                <Col span={24}>
                                    <Form.Item name="customerEmail" label="Email Address">
                                        <Input placeholder="john@example.com" />
                                    </Form.Item>
                                </Col>
                            </Row>

                            <Divider>Device Details</Divider>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item name="deviceModel" label="Device Model" rules={[{ required: true }]}>
                                        <Select placeholder="Select Model">
                                            <Option value="iPhone 13">iPhone 13</Option>
                                            <Option value="iPhone 13 Pro">iPhone 13 Pro</Option>
                                            <Option value="iPhone 14">iPhone 14</Option>
                                            <Option value="Samsung S22">Samsung S22</Option>
                                        </Select>
                                    </Form.Item>
                                </Col>
                                <Col span={12}>
                                    <Form.Item name="issueType" label="Issue / Fault" rules={[{ required: true }]}>
                                        <Select placeholder="Select Issue">
                                            <Option value="Screen Crack">Screen Crack</Option>
                                            <Option value="Battery Replacement">Battery Replacement</Option>
                                            <Option value="Water Damage">Water Damage</Option>
                                            <Option value="Charging Port">Charging Port</Option>
                                        </Select>
                                    </Form.Item>
                                </Col>
                                <Col span={24}>
                                    <Form.Item name="isbn_imei" label="IMEI / Serial (Optional)">
                                        <Input placeholder="Device IMEI or Serial Number" />
                                    </Form.Item>
                                </Col>
                            </Row>
                        </Form>
                    </Card>
                </Col>

                <Col xs={24} lg={8}>
                    <Card className="shadow-sm rounded-xl mb-6 bg-gray-50 border-gray-200">
                        <Statistic
                            title="Estimated Repair Cost"
                            value={estimatedPrice !== null ? estimatedPrice : '--'}
                            prefix="$"
                            styles={{ content: { color: estimatedPrice ? '#cf1322' : '#999', fontWeight: 'bold' } }}
                        />
                        <Text type="secondary" className="text-xs mt-2 block">
                            *Final price may vary upon inspection.
                        </Text>
                    </Card>

                    <Card title="Actions" className="shadow-sm rounded-xl">
                        <Button
                            block
                            size="large"
                            type="primary"
                            danger
                            icon={<ToolOutlined />}
                            onClick={handleProceed}
                            className="mb-4 h-12 text-lg font-bold"
                            disabled={!model || !issue}
                        >
                            Proceed to Repair
                        </Button>
                        <Button
                            block
                            size="large"
                            icon={<SaveOutlined />}
                            onClick={() => form.validateFields().then(handleSaveLead)}
                            loading={loading}
                        >
                            Customer Declined
                        </Button>
                    </Card>
                </Col>
            </Row>
        </div>
    );
}

"use client";

import React, { useState, useEffect } from 'react';
import { Layout, Menu, theme, App as AntdApp } from 'antd';
import {
    ToolOutlined,
    CarOutlined,
    LogoutOutlined,
    MedicineBoxOutlined
} from '@ant-design/icons';
import { useRouter, usePathname } from 'next/navigation';
import { User } from '@/lib/types';

const { Header, Sider, Content } = Layout;

export default function AuthenticatedLayout({
    children,
}: {
    children: React.ReactNode;
}) {
    const [collapsed, setCollapsed] = useState(false);
    const [user, setUser] = useState<User | null>(null);
    const router = useRouter();

    useEffect(() => {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
            // eslint-disable-next-line react-hooks/exhaustive-deps
            setUser(JSON.parse(storedUser));
        }
    }, []);
    const pathname = usePathname();
    const {
        token: { colorBgContainer, borderRadiusLG },
    } = theme.useToken();

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        router.push('/login');
    };

    return (
        <AntdApp>
            <Layout style={{ minHeight: '100vh' }}>
                <Sider trigger={null} collapsible collapsed={collapsed} onCollapse={setCollapsed} theme="light">
                    <div className="demo-logo-vertical" style={{ height: 64, margin: 16, background: '#E60000', borderRadius: 6, display: 'flex', alignItems: 'center', justifyContent: 'center', color: 'white', fontWeight: 'bold' }}>
                        {collapsed ? 'SPR' : 'SPR ERP'}
                    </div>
                    <Menu
                        theme="light"
                        mode="inline"
                        selectedKeys={[pathname]}
                        onClick={({ key }) => {
                            if (key === 'logout') handleLogout();
                            else router.push(key);
                        }}
                        items={[
                            {
                                key: '/diagnostic',
                                icon: <MedicineBoxOutlined />,
                                label: 'Diagnostic',
                            },
                            {
                                key: '/repairs',
                                icon: <ToolOutlined />,
                                label: 'Repairs',
                            },
                            {
                                key: '/logistics',
                                icon: <CarOutlined />,
                                label: 'Logistics',
                            },
                            {
                                key: 'logout',
                                icon: <LogoutOutlined />,
                                label: 'Logout',
                                danger: true,
                            }
                        ]}
                    />
                </Sider>
                <Layout>
                    <Header style={{ padding: 0, background: colorBgContainer, display: 'flex', alignItems: 'center', justifyContent: 'space-between', paddingRight: 24, paddingLeft: 24 }}>
                        <div style={{ display: 'flex', alignItems: 'center' }}>
                            {user && (
                                <div style={{ fontWeight: '600', color: '#555', fontSize: '14px' }}>
                                    Current ID: <span style={{ color: '#E60000' }}>{user.fullName}</span>
                                    <span style={{ marginLeft: 8, padding: '2px 8px', background: '#f0f0f0', borderRadius: '4px', fontSize: '12px', color: '#888' }}>
                                        {user.username}
                                    </span>
                                </div>
                            )}
                        </div>
                    </Header>
                    <Content
                        style={{
                            margin: '24px 16px',
                            padding: 24,
                            minHeight: 280,
                            background: colorBgContainer,
                            borderRadius: borderRadiusLG,
                        }}
                    >
                        {children}
                    </Content>
                </Layout>
            </Layout>
        </AntdApp>
    );
}

"use client";

import React, { useEffect, useState } from 'react';
import { Card, Table, Tag, Button, Space, Descriptions, Divider, Typography, message, App, Row, Col } from 'antd';
import { ArrowLeftOutlined, CheckCircleOutlined, CarOutlined } from '@ant-design/icons';
import { useRouter, useParams } from 'next/navigation';
import api from '@/lib/axios';
import { formatDate } from '@/lib/utils';

const { Title, Text } = Typography;

export default function BatchDetailPage() {
    const [batch, setBatch] = useState<any>(null);
    const [loading, setLoading] = useState(false);
    const router = useRouter();
    const { batchNo } = useParams();

    useEffect(() => {
        fetchBatch();
    }, [batchNo]);

    const fetchBatch = async () => {
        setLoading(true);
        try {
            const { data } = await api.get(`/logistics/batches/${batchNo}`);
            setBatch(data);
        } catch (error) {
            console.error('Failed to fetch batch:', error);
            message.error('Batch not found');
        } finally {
            setLoading(false);
        }
    };

    const handlePickup = async () => {
        try {
            await api.post('/logistics/scan/pickup', { batchNo });
            message.success('Batch status updated to IN TRANSIT');
            fetchBatch();
        } catch (error: any) {
            message.error(error.response?.data?.message || 'Failed to update status');
        }
    };

    if (!batch) return <div className="p-6">Loading batch details...</div>;

    const columns = [
        {
            title: 'Order No.',
            dataIndex: ['repairOrder', 'orderNo'],
            key: 'orderNo',
            render: (text: string) => <span className="font-bold">{text}</span>
        },
        {
            title: 'Model',
            dataIndex: ['repairOrder', 'model'],
            key: 'model'
        },
        {
            title: 'Current Status',
            dataIndex: ['repairOrder', 'status'],
            key: 'status',
            render: (status: string) => <Tag>{status}</Tag>
        }
    ];

    return (
        <App>
            <div className="p-0">
                <div className="flex justify-between items-center mb-6">
                    <Space>
                        <Button icon={<ArrowLeftOutlined />} onClick={() => router.push('/logistics')} />
                        <Title level={3} style={{ margin: 0 }}>Batch Details: {batchNo}</Title>
                    </Space>
                    <Space>
                        {batch.status === 'CREATED' && (
                            <Button
                                type="primary"
                                icon={<CarOutlined />}
                                onClick={handlePickup}
                                className="bg-black"
                            >
                                Mark as Pickup (In Transit)
                            </Button>
                        )}
                    </Space>
                </div>

                <Row gutter={16}>
                    <Col span={24}>
                        <Card variant="borderless" className="mb-6">
                            <Descriptions title="Transport Information" bordered>
                                <Descriptions.Item label="SPR ID">{batch.batchNo}</Descriptions.Item>
                                <Descriptions.Item label="Direction">
                                    <Tag color={batch.direction === 'TO_HQ' ? 'orange' : 'blue'}>
                                        {batch.direction === 'TO_HQ' ? 'To HQ' : 'To Branch'}
                                    </Tag>
                                </Descriptions.Item>
                                <Descriptions.Item label="Current Status">
                                    <Tag color={batch.status === 'CREATED' ? 'blue' : 'green'}>{batch.status}</Tag>
                                </Descriptions.Item>
                                <Descriptions.Item label="Creator">{batch.creator?.fullName}</Descriptions.Item>
                                <Descriptions.Item label="Date">{formatDate(batch.createdAt)}</Descriptions.Item>
                            </Descriptions>
                        </Card>

                        <Card title="Repairs in this Batch" variant="borderless">
                            <Table
                                dataSource={batch.items}
                                columns={columns}
                                rowKey="id"
                                pagination={false}
                            />
                        </Card>
                    </Col>
                </Row>
            </div>
        </App>
    );
}

"use client";

import React, { useEffect, useState } from 'react';
import { Table, Button, Select, Space, Card, Typography, message, Tag } from 'antd';
import { CarOutlined, ArrowLeftOutlined, SaveOutlined } from '@ant-design/icons';
import { useRouter } from 'next/navigation';
import api from '@/lib/axios';

const { Title } = Typography;
const { Option } = Select;

export default function CreateBatchPage() {
    const [orders, setOrders] = useState([]);
    const [selectedIds, setSelectedIds] = useState<number[]>([]);
    const [direction, setDirection] = useState<'TO_HQ' | 'TO_BRANCH'>('TO_HQ');
    const [loading, setLoading] = useState(false);
    const [submitting, setSubmitting] = useState(false);
    const router = useRouter();

    useEffect(() => {
        fetchAvailableOrders();
    }, [direction]);

    const fetchAvailableOrders = async () => {
        setLoading(true);
        try {
            const { data } = await api.get('/repairs');
            // Filter based on direction
            const filtered = data.filter((order: any) => {
                if (direction === 'TO_HQ') {
                    return order.status === 'IN_BRANCH';
                } else {
                    return order.status === 'REPAIRED' || order.status === 'REPAIR_FAILED';
                }
            });
            setOrders(filtered);
            setSelectedIds([]); // Reset selection when direction changes
        } catch (error) {
            console.error('Failed to fetch orders:', error);
            message.error('Failed to load available orders');
        } finally {
            setLoading(false);
        }
    };

    const handleCreate = async () => {
        if (selectedIds.length === 0) {
            message.warning('Please select at least one order');
            return;
        }

        setSubmitting(true);
        try {
            await api.post('/logistics/batches', {
                ids: selectedIds,
                direction
            });
            message.success('Transport batch created successfully');
            router.push('/logistics');
        } catch (error: any) {
            message.error(error.response?.data?.message || 'Failed to create batch');
        } finally {
            setSubmitting(false);
        }
    };

    const columns = [
        {
            title: 'Order No.',
            dataIndex: 'orderNo',
            render: (text: string) => <span className="font-bold">{text}</span>
        },
        {
            title: 'Model',
            dataIndex: 'model'
        },
        {
            title: 'Status',
            dataIndex: 'status',
            render: (status: string) => <Tag>{status}</Tag>
        },
        {
            title: 'Current Branch',
            dataIndex: 'branch',
            render: (branch: any) => branch?.name || '-'
        }
    ];

    return (
        <div className="max-w-5xl mx-auto py-2" suppressHydrationWarning>
            <div className="flex items-center gap-4 mb-6">
                <Button
                    icon={<ArrowLeftOutlined />}
                    onClick={() => router.back()}
                    type="text"
                    className="hover:bg-gray-100"
                />
                <h2 className="text-2xl font-bold m-0">New Transport Batch</h2>
            </div>

            <Card className="mb-6">
                <div className="flex flex-col md:flex-row md:items-center justify-between gap-6">
                    <div className="flex items-center gap-4">
                        <span className="font-semibold text-gray-700">Direction:</span>
                        <Select
                            value={direction}
                            onChange={setDirection}
                            style={{ width: 240 }}
                            size="large"
                        >
                            <Option value="TO_HQ">To HQ (Collection)</Option>
                            <Option value="TO_BRANCH">To Branch (Returning)</Option>
                        </Select>
                        <Tag color="red" className="ml-2 px-3 py-1 font-bold">
                            {orders.length} Available
                        </Tag>
                    </div>

                    <Button
                        type="primary"
                        icon={<SaveOutlined />}
                        size="large"
                        style={{ backgroundColor: '#E60000' }}
                        className="h-12 px-8 font-bold border-none"
                        onClick={handleCreate}
                        loading={submitting}
                    >
                        Save & Generate SPR {selectedIds.length > 0 && `(${selectedIds.length})`}
                    </Button>
                </div>
            </Card>

            <Card title="Select Items for Batch">
                <Table
                    rowSelection={{
                        type: 'checkbox',
                        selectedRowKeys: selectedIds,
                        onChange: (keys) => setSelectedIds(keys as number[])
                    }}
                    columns={columns}
                    dataSource={orders}
                    rowKey="id"
                    loading={loading}
                    pagination={{ pageSize: 15, hideOnSinglePage: true }}
                />
            </Card>
        </div>
    );
}

"use client";

import React, { useEffect, useState } from 'react';
import { Table, Tag, Button, Space, Card, Typography } from 'antd';
import { CarOutlined, PlusOutlined, EyeOutlined } from '@ant-design/icons';
import { useRouter } from 'next/navigation';
import api from '@/lib/axios';
import { formatDate } from '@/lib/utils';

const { Title } = Typography;

import BatchDetailModal from '@/components/BatchDetailModal';
import CreateBatchModal from '@/components/CreateBatchModal';

export default function LogisticsPage() {
    const [batches, setBatches] = useState([]);
    const [loading, setLoading] = useState(false);

    // Modal states
    const [selectedBatchNo, setSelectedBatchNo] = useState<string | null>(null);
    const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);

    useEffect(() => {
        fetchBatches();
    }, []);

    const fetchBatches = async () => {
        setLoading(true);
        try {
            const { data } = await api.get('/logistics/batches');
            setBatches(data);
        } catch (error) {
            console.error('Failed to fetch batches:', error);
        } finally {
            setLoading(false);
        }
    };

    const columns = [
        {
            title: 'Batch No.',
            dataIndex: 'batchNo',
            key: 'batchNo',
            render: (text: string) => <span className="font-bold">{text}</span>
        },
        {
            title: 'Direction',
            dataIndex: 'direction',
            key: 'direction',
            render: (direction: string) => (
                <Tag color={direction === 'TO_HQ' ? 'orange' : 'blue'} variant="filled" className="font-bold">
                    {direction === 'TO_HQ' ? 'TO HQ' : 'TO BRANCH'}
                </Tag>
            )
        },
        {
            title: 'Status',
            dataIndex: 'status',
            key: 'status',
            render: (status: string) => {
                let color = 'default';
                if (status === 'CREATED') color = 'blue';
                if (status === 'IN_TRANSIT') color = 'processing';
                if (status === 'RECEIVED') color = 'success';
                return <Tag color={color} variant="filled" className="font-bold">{status}</Tag>;
            }
        },
        {
            title: 'Created At',
            dataIndex: 'createdAt',
            key: 'createdAt',
            render: (date: string) => formatDate(date)
        },
        {
            title: 'Actions',
            key: 'actions',
            render: (_: any, record: any) => (
                <Button
                    type="link"
                    icon={<EyeOutlined />}
                    onClick={() => setSelectedBatchNo(record.batchNo)}
                    className="font-medium"
                >
                    View
                </Button>
            )
        }
    ];

    return (
        <div suppressHydrationWarning>
            <div className="flex justify-between items-center mb-6">
                <div>
                    <h2 className="text-2xl font-bold m-0">Logistics Management</h2>
                    <p className="text-gray-400 text-sm">Transport batches and real-time tracking</p>
                </div>
                <Button
                    type="primary"
                    icon={<PlusOutlined />}
                    style={{ backgroundColor: '#E60000' }}
                    className="h-10 px-6 font-bold border-none"
                    onClick={() => setIsCreateModalOpen(true)}
                >
                    Create Batch
                </Button>
            </div>

            <Card className="shadow-sm rounded-xl border-gray-100">
                <Table
                    columns={columns}
                    dataSource={batches}
                    rowKey="id"
                    loading={loading}
                    pagination={{ pageSize: 12 }}
                />
            </Card>

            <BatchDetailModal
                batchNo={selectedBatchNo}
                open={selectedBatchNo !== null}
                onCancel={() => setSelectedBatchNo(null)}
            />

            <CreateBatchModal
                open={isCreateModalOpen}
                onCancel={() => setIsCreateModalOpen(false)}
                onSuccess={() => {
                    setIsCreateModalOpen(false);
                    fetchBatches();
                }}
            />
        </div>
    );
}

"use client";

import React, { useEffect, useState } from 'react';
import { Card, Descriptions, Tag, Timeline, Button, Modal, Form, Select, Input, message, Spin, Space, Popconfirm } from 'antd';
import { useParams, useRouter } from 'next/navigation';
import api from '@/lib/axios';
import { useAuth } from '@/hooks/useAuth';
import { formatDate } from '@/lib/utils';
import { ClockCircleOutlined, CheckCircleOutlined, CloseCircleOutlined } from '@ant-design/icons';

export default function RepairDetails() {
    const { id } = useParams();
    const router = useRouter();
    const { user, hasRole } = useAuth();
    const [order, setOrder] = useState<any>(null);
    const [loading, setLoading] = useState(false);

    // Modals
    const [isAssignModalOpen, setIsAssignModalOpen] = useState(false);
    const [isFailModalOpen, setIsFailModalOpen] = useState(false);
    const [technicians, setTechnicians] = useState<any[]>([]); // Need an API for this
    const [actionLoading, setActionLoading] = useState(false);

    useEffect(() => {
        if (id) fetchOrder();
    }, [id]);

    const fetchOrder = async () => {
        setLoading(true);
        try {
            const { data } = await api.get(`/repairs/${id}`);
            setOrder(data);
        } catch (e) {
            console.error(e);
            message.error('Failed to load order');
        } finally {
            setLoading(false);
        }
    };

    const loadTechnicians = async () => {
        // We need an endpoint to list technicians. 
        // For MVP, if we don't have it, we might need to add it or just input ID manually?
        // Design Spec says: "Assign to Technician".
        // Let's assume we can fetch users by role or list all users.
        // I implemented `UsersController.findAll` but restricted to HQ/Super.
        // If current user is HQ, they can fetch list.
        try {
            const { data } = await api.get('/users');
            // Filter frontend side or backend side? Backend `findAll` returns all users.
            setTechnicians(data.filter((u: any) => u.role === 'TECHNICIAN'));
        } catch (e) {
            console.error(e);
        }
    };

    const handleAction = async (action: string, payload: any = {}) => {
        setActionLoading(true);
        try {
            if (action === 'assign') {
                await api.patch(`/repairs/${id}/assign`, payload);
                setIsAssignModalOpen(false);
            } else if (action === 'start') {
                await api.patch(`/repairs/${id}/start`);
            } else if (action === 'complete') {
                await api.patch(`/repairs/${id}/complete`);
            } else if (action === 'fail') {
                await api.patch(`/repairs/${id}/fail`, payload);
                setIsFailModalOpen(false);
            }
            message.success('Action successful');
            fetchOrder();
        } catch (e: any) {
            message.error(e.response?.data?.message || 'Action failed');
        } finally {
            setActionLoading(false);
        }
    };

    if (loading || !order) return <Spin className="block mx-auto mt-10" />;

    return (
        <div className="max-w-4xl mx-auto">
            <div className="flex justify-between mb-4">
                <h2 className="text-2xl font-bold">Order #{order.orderNo}</h2>
                <Space>
                    {/* HQ Actions */}
                    {hasRole(['HQ_ADMIN', 'SUPER_ADMIN']) && order.status === 'AT_HQ' && (
                        <Button type="primary" onClick={() => { setIsAssignModalOpen(true); loadTechnicians(); }}>
                            Assign Technician
                        </Button>
                    )}

                    {/* Technician Actions */}
                    {hasRole(['TECHNICIAN']) && order.status === 'ASSIGNED_TO_TECHNICIAN' && user.id === order.technicianId && (
                        <Button type="primary" onClick={() => handleAction('start')}>
                            Start Repair
                        </Button>
                    )}

                    {hasRole(['TECHNICIAN']) && order.status === 'UNDER_REPAIR' && user.id === order.technicianId && (
                        <>
                            <Popconfirm title="Confirm Success?" onConfirm={() => handleAction('complete')}>
                                <Button type="primary" style={{ backgroundColor: 'green' }}>Mark Repaired</Button>
                            </Popconfirm>
                            <Button danger onClick={() => setIsFailModalOpen(true)}>Mark Failed</Button>
                        </>
                    )}
                </Space>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <div className="col-span-2">
                    <Card title="Device Info" className="mb-6">
                        <Descriptions bordered column={1}>
                            <Descriptions.Item label="Model">{order.deviceModel}</Descriptions.Item>
                            <Descriptions.Item label="IMEI/SN">{order.isbn_imei || '-'}</Descriptions.Item>
                            <Descriptions.Item label="Problem">{order.problemDesc}</Descriptions.Item>
                            <Descriptions.Item label="Branch">{order.branch?.name}</Descriptions.Item>
                            <Descriptions.Item label="Status"><Tag color="blue">{order.status}</Tag></Descriptions.Item>
                            <Descriptions.Item label="Technician">{order.technician?.fullName || 'Unassigned'}</Descriptions.Item>
                        </Descriptions>
                    </Card>
                </div>

                <div className="col-span-1">
                    <Card title="History">
                        <Timeline
                            mode="start"
                            items={order.history?.map((log: any) => ({
                                color: log.toStatus === 'REPAIR_FAILED' ? 'red' : 'blue',
                                icon: log.isScanAction ? <CheckCircleOutlined /> : <ClockCircleOutlined />,
                                title: <span className="font-bold text-xs">{formatDate(log.createdAt)}</span>,
                                content: (
                                    <>
                                        <p className="font-semibold m-0">{log.toStatus.replace(/_/g, ' ')}</p>
                                        <p className="text-gray-500 text-xs m-0">{log.remark} by {log.user?.fullName}</p>
                                    </>
                                )
                            }))}
                        />
                    </Card>
                </div>
            </div>

            {/* Assign Modal */}
            <Modal
                title="Assign Technician"
                open={isAssignModalOpen}
                onCancel={() => setIsAssignModalOpen(false)}
                footer={null}
            >
                <Form onFinish={(vals) => handleAction('assign', vals)} layout="vertical">
                    <Form.Item name="technicianId" label="Select Technician" rules={[{ required: true }]}>
                        <Select placeholder="Choose one...">
                            {technicians.map(t => (
                                <Select.Option key={t.id} value={t.id}>{t.fullName} ({t.username})</Select.Option>
                            ))}
                        </Select>
                    </Form.Item>
                    <Button type="primary" htmlType="submit" loading={actionLoading} block>Assign</Button>
                </Form>
            </Modal>

            {/* Fail Modal */}
            <Modal
                title="Mark Repair Failed"
                open={isFailModalOpen}
                onCancel={() => setIsFailModalOpen(false)}
                footer={null}
            >
                <Form onFinish={(vals) => handleAction('fail', vals)} layout="vertical">
                    <Form.Item name="reason" label="Failure Reason" rules={[{ required: true }]}>
                        <Input.TextArea rows={3} placeholder="Why cannot be repaired?" />
                    </Form.Item>
                    <Button danger type="primary" htmlType="submit" loading={actionLoading} block>Confirm Failed</Button>
                </Form>
            </Modal>
        </div>
    );
}

"use client";

import React, { useState, useEffect } from 'react';
import { Form, Input, Button, Card, Divider, App } from 'antd';
import { UserOutlined, ShopOutlined, AlertOutlined } from '@ant-design/icons';
import api from '@/lib/axios';
import { useRouter } from 'next/navigation';

export default function CreateRepairOrder() {
    const [loading, setLoading] = useState(false);
    const router = useRouter();
    const [form] = Form.useForm();
    const { message } = App.useApp();

    useEffect(() => {
        // Check for data from Diagnostic page
        const diagnosticData = sessionStorage.getItem('diagnostic_data');
        if (diagnosticData) {
            try {
                const parsedData = JSON.parse(diagnosticData);
                form.setFieldsValue({
                    ...parsedData,
                    // Map diagnostic fields if names differ, but they match our design
                });
                message.success('Data imported from Diagnostic session');
                // Optional: Clear it so it doesn't persist on refresh unintentionally, 
                // but keeping it might be safer for refresh. remove it after submit.
            } catch (e) {
                console.error("Failed to parse diagnostic data", e);
            }
        }
    }, [form, message]);

    const onFinish = async (values: any) => {
        setLoading(true);
        try {
            await api.post('/repairs', values);
            message.success('Repair Order Created Successfully');

            // Clear diagnostic session data on success
            sessionStorage.removeItem('diagnostic_data');

            router.push('/repairs');
        } catch (error: any) {
            console.error(error);
            message.error(error.response?.data?.message || 'Failed to create order');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="max-w-2xl mx-auto" suppressHydrationWarning>
            <h2 className="text-2xl font-bold mb-6">New Repair Order</h2>
            <Card>
                <Form
                    form={form}
                    layout="vertical"
                    onFinish={onFinish}
                >
                    <Divider><UserOutlined /> Customer Information</Divider>
                    <Form.Item
                        name="customerName"
                        label="Customer Name"
                        rules={[{ required: true }]}
                    >
                        <Input placeholder="Customer Name" />
                    </Form.Item>
                    <Form.Item
                        name="customerContact"
                        label="Contact Number"
                        rules={[{ required: true }]}
                    >
                        <Input placeholder="Phone Number" />
                    </Form.Item>
                    <Form.Item
                        name="customerEmail"
                        label="Email Address"
                    >
                        <Input placeholder="Email (Optional)" />
                    </Form.Item>

                    <Divider><ShopOutlined /> Device Information</Divider>
                    <Form.Item
                        label="Repair Order ID"
                        tooltip="System generated ID"
                    >
                        <Input placeholder="System Generated (Auto-filled)" disabled className="bg-gray-100 text-gray-500 font-mono" />
                    </Form.Item>

                    <Form.Item
                        name="deviceModel"
                        label="Device Model"
                        rules={[{ required: true }]}
                    >
                        <Input placeholder="e.g. iPhone 13 Pro" />
                    </Form.Item>

                    <Form.Item
                        name="isbn_imei"
                        label="IMEI / Serial Number"
                    >
                        <Input placeholder="Optional" />
                    </Form.Item>

                    <Divider><AlertOutlined /> Issue Details</Divider>
                    <Form.Item
                        name="issueType"
                        label="Primary Issue"
                    >
                        <Input placeholder="e.g. Screen Crack" />
                    </Form.Item>

                    <Form.Item
                        name="problemDesc"
                        label="Detailed Description"

                    >
                        <Input.TextArea rows={4} placeholder="Describe the issue..." />
                    </Form.Item>

                    <Form.Item>
                        <Button type="primary" htmlType="submit" loading={loading} style={{ backgroundColor: '#E60000' }} block size="large">
                            Confirm & Create Order
                        </Button>
                        <Button type="default" className="mt-2" block onClick={() => router.back()}>
                            Cancel
                        </Button>
                    </Form.Item>
                </Form>
            </Card>
        </div>
    );
}

"use client";

import React, { useEffect, useState } from 'react';
import { Table, Tag, Button, Card, Space, Input } from 'antd';
import { EyeOutlined } from '@ant-design/icons';
import { useRouter } from 'next/navigation';
import api from '@/lib/axios';
import { formatDate } from '@/lib/utils';
import { ColumnType } from 'antd/es/table';

interface RepairOrder {
    id: number;
    orderNo: string;
    deviceModel: string;
    problemDesc: string;
    status: string;
    branch?: { name: string };
    technician?: { fullName: string };
    createdAt: string;
}


import RepairDetailModal from '@/components/RepairDetailModal';

export default function RepairsList() {
    const [data, setData] = useState<RepairOrder[]>([]);
    const [loading, setLoading] = useState(false);
    const [searchText, setSearchText] = useState('');

    // Modal states
    const [selectedOrderId, setSelectedOrderId] = useState<number | null>(null);

    const fetchRepairs = async () => {
        setLoading(true);
        try {
            const { data } = await api.get('/repairs');
            setData(data);
        } catch (error) {
            console.error(error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchRepairs();
    }, []);

    const getStatusColor = (status: string) => {
        switch (status) {
            case 'IN_BRANCH': return 'blue';
            case 'REPAIRED': return 'green';
            case 'REPAIR_FAILED': return 'red';
            case 'COMPLETED': return 'gray';
            case 'UNDER_REPAIR': return 'orange';
            default: return 'default';
        }
    };

    const columns: ColumnType<RepairOrder>[] = [
        {
            title: 'Order No',
            dataIndex: 'orderNo',
            key: 'orderNo',
            render: (text) => <span className="font-bold">{text}</span>
        },
        {
            title: 'Device',
            dataIndex: 'deviceModel',
            key: 'deviceModel',
        },
        {
            title: 'Branch',
            dataIndex: ['branch', 'name'],
            key: 'branch',
        },
        {
            title: 'Status',
            dataIndex: 'status',
            key: 'status',
            render: (status: string) => (
                <Tag color={getStatusColor(status)} key={status} variant="filled" className="font-medium">
                    {status.replace(/_/g, ' ')}
                </Tag>
            ),
        },
        {
            title: 'Technician',
            dataIndex: ['technician', 'fullName'],
            key: 'technician',
            render: (text) => text || '-',
        },
        {
            title: 'Created At',
            dataIndex: 'createdAt',
            key: 'createdAt',
            render: (date) => formatDate(date),
        },
        {
            title: 'Action',
            key: 'action',
            render: (_, record) => (
                <Space size="middle">
                    <Button
                        type="link"
                        icon={<EyeOutlined />}
                        onClick={() => setSelectedOrderId(record.id)}
                        className="text-blue-600 font-medium"
                    >
                        View
                    </Button>
                </Space>
            ),
        },
    ];

    const filteredData = data.filter((item) => {
        const search = searchText.toLowerCase();
        return (
            item.orderNo.toLowerCase().includes(search) ||
            item.deviceModel.toLowerCase().includes(search) ||
            item.branch?.name.toLowerCase().includes(search)
        );
    });

    return (
        <div suppressHydrationWarning>
            <div className="flex justify-between items-center mb-6">
                <h2 className="text-2xl font-bold">Repair Orders</h2>
                <Space size="large">
                    <Input.Search
                        placeholder="Search order no or device..."
                        allowClear
                        onSearch={(value) => setSearchText(value)}
                        onChange={(e) => setSearchText(e.target.value)}
                        style={{ width: 340 }}
                        className="rounded-lg"
                    />

                </Space>
            </div>

            <Card className="shadow-sm rounded-xl border-gray-100">
                <Table
                    columns={columns}
                    dataSource={filteredData}
                    rowKey="id"
                    loading={loading}
                    pagination={{ pageSize: 12 }}
                />
            </Card>

            {/* Modals */}


            <RepairDetailModal
                orderId={selectedOrderId}
                open={selectedOrderId !== null}
                onCancel={() => setSelectedOrderId(null)}
                onActionSuccess={fetchRepairs}
            />
        </div>
    );
}

"use client";

import React, { useEffect, useState, Suspense } from 'react';
import { Input, Button, Card, message, App, List, Tag, Spin, Typography, Space } from 'antd';
import { ScanOutlined, CheckCircleOutlined, CarOutlined, LoadingOutlined } from '@ant-design/icons';
import { Html5QrcodeScanner } from 'html5-qrcode';
import { useSearchParams, useRouter } from 'next/navigation';
import api from '@/lib/axios';
import { formatDate } from '@/lib/utils';

const { Title, Text } = Typography;

function ScanContent() {
    const [loading, setLoading] = useState(false);
    const [actionLoading, setActionLoading] = useState(false);
    const [batchNo, setBatchNo] = useState('');
    const [previewData, setPreviewData] = useState<any>(null);
    const [manualCode, setManualCode] = useState('');
    const searchParams = useSearchParams();
    const router = useRouter();
    const { message: antdMsg } = App.useApp();

    useEffect(() => {
        const urlBatch = searchParams.get('batchNo');
        if (urlBatch) {
            setBatchNo(urlBatch);
            fetchPreview(urlBatch);
        }

        const scanner = new Html5QrcodeScanner(
            "reader",
            { fps: 10, qrbox: { width: 250, height: 250 } },
            false
        );

        scanner.render((decodedText) => {
            let extracted = decodedText;
            try {
                if (decodedText.includes('batchNo=')) {
                    extracted = new URL(decodedText).searchParams.get('batchNo') || decodedText;
                }
            } catch (e) { }
            setBatchNo(extracted);
            fetchPreview(extracted);
            scanner.clear();
        }, (error) => { });

        return () => {
            scanner.clear().catch(() => { });
        };
    }, [searchParams]);

    const fetchPreview = async (code: string) => {
        setLoading(true);
        try {
            const { data } = await api.get(`/logistics/batches/${code}`);
            setPreviewData(data);
        } catch (e: any) {
            antdMsg.error(e.response?.data?.message || 'Invalid Batch QR');
            setPreviewData(null);
        } finally {
            setLoading(false);
        }
    };

    const handleAction = async () => {
        if (!previewData) return;
        setActionLoading(true);
        try {
            const userStr = localStorage.getItem('user');
            const user = userStr ? JSON.parse(userStr) : null;

            if (user?.role === 'COURIER') {
                await api.post('/logistics/scan/pickup', { batchNo: previewData.batchNo });
                antdMsg.success(` Pickup Confirmed: Batch ${previewData.batchNo} is now in transit.`);
            } else {
                await api.post('/logistics/scan/receive', { batchNo: previewData.batchNo });
                antdMsg.success(` Receive Confirmed: Batch ${previewData.batchNo} has been delivered.`);
            }
            router.push('/logistics');
        } catch (e: any) {
            antdMsg.error(e.response?.data?.message || 'Action Failed');
        } finally {
            setActionLoading(false);
        }
    };

    return (
        <div className="max-w-2xl mx-auto py-8 px-4">
            <Title level={2} className="text-center mb-8">Logistics Scanning</Title>

            {!previewData && (
                <Card className="mb-6 shadow-md rounded-2xl overflow-hidden border-none">
                    <div id="reader" className="w-full"></div>
                    <div className="p-4 text-center text-gray-400">
                        Please point your camera at the SPR Transport QR Code
                    </div>
                </Card>
            )}

            {loading && <div className="text-center py-10"><Spin indicator={<LoadingOutlined style={{ fontSize: 40 }} spin />} /></div>}

            {previewData && (
                <Card
                    className="mb-6 shadow-xl border-none rounded-3xl overflow-hidden"
                    title={
                        <div className="flex justify-between items-center py-2">
                            <span className="text-lg font-black">BATCH VERIFICATION</span>
                            <Tag color="red" variant="filled" className="m-0">{previewData.direction.replace('_', ' ')}</Tag>
                        </div>
                    }
                >
                    <div className="bg-gray-50 p-6 rounded-2xl mb-6">
                        <div className="grid grid-cols-2 gap-y-4">
                            <div>
                                <Text type="secondary" className="block text-xs uppercase font-bold tracking-widest">Batch Number</Text>
                                <Text className="text-xl font-black">{previewData.batchNo}</Text>
                            </div>
                            <div>
                                <Text type="secondary" className="block text-xs uppercase font-bold tracking-widest">Current Status</Text>
                                <Tag color="processing" variant="filled" className="font-bold">{previewData.status}</Tag>
                            </div>
                            <div>
                                <Text type="secondary" className="block text-xs uppercase font-bold tracking-widest">Created On</Text>
                                <Text className="font-medium">{formatDate(previewData.createdAt)}</Text>
                            </div>
                            <div>
                                <Text type="secondary" className="block text-xs uppercase font-bold tracking-widest">Items Count</Text>
                                <Text className="font-black text-lg">{previewData.items?.length || 0}</Text>
                            </div>
                        </div>
                    </div>

                    <Title level={4} className="flex items-center gap-2 mb-4">
                        <CarOutlined className="text-red-600" /> Included Repair Orders
                    </Title>
                    <List
                        dataSource={previewData.items}
                        className="bg-white rounded-xl border border-gray-100 mb-8"
                        renderItem={(item: any) => (
                            <List.Item className="px-4 border-b last:border-none flex justify-between">
                                <span className="font-bold">{item.repairOrder?.orderNo}</span>
                                <span className="text-gray-500">{item.repairOrder?.deviceModel}</span>
                                <Tag color="blue" variant="filled" className="text-[10px] font-bold">{item.repairOrder?.status}</Tag>
                            </List.Item>
                        )}
                    />

                    <div className="flex flex-col gap-3">
                        <Button
                            type="primary"
                            size="large"
                            block
                            className="h-16 text-lg font-black bg-red-600 hover:bg-black border-none rounded-2xl shadow-lg ring-4 ring-red-100"
                            onClick={handleAction}
                            loading={actionLoading}
                            icon={<CheckCircleOutlined />}
                        >
                            CONFIRM & UPDATE STATUS
                        </Button>
                        <Button size="large" block variant="outlined" className="h-12 font-bold rounded-2xl text-gray-400" onClick={() => { setPreviewData(null); setBatchNo(''); }}>
                            Cancel / Scan Another
                        </Button>
                    </div>
                </Card>
            )}

            {!previewData && !loading && (
                <Card title="Manual Correction" className="shadow-sm rounded-2xl border-gray-100">
                    <Space.Compact className="w-full h-12">
                        <Input
                            placeholder="Enter Batch No manually..."
                            value={manualCode}
                            onChange={(e) => setManualCode(e.target.value)}
                        />
                        <Button type="primary" className="bg-black border-none font-bold" onClick={() => fetchPreview(manualCode)}>Load</Button>
                    </Space.Compact>
                </Card>
            )}
        </div>
    );
}

export default function ScanPage() {
    return (
        <Suspense fallback={<div className="text-center py-20"><Spin size="large" /></div>}>
            <ScanContent />
        </Suspense>
    );
}

