"use client";

import React, { useEffect, useState } from 'react';
import { Table, Button, Modal, Form, Input, Space, Popconfirm, Card, App, Tag, Checkbox } from 'antd';
import { PlusOutlined, DeleteOutlined, ShopOutlined, EditOutlined } from '@ant-design/icons';
import api from '@/lib/axios';

export default function BranchesPage() {
    const [branches, setBranches] = useState([]);
    const [loading, setLoading] = useState(false);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingBranch, setEditingBranch] = useState<any>(null);
    const [form] = Form.useForm();
    const { message } = App.useApp();

    useEffect(() => {
        fetchBranches();
    }, []);

    const fetchBranches = async () => {
        setLoading(true);
        try {
            const { data } = await api.get('/branches');
            setBranches(data);
        } catch (e) {
            message.error('Failed to load branches');
        } finally {
            setLoading(false);
        }
    };

    const handleSubmit = async (values: any) => {
        try {
            if (editingBranch) {
                await api.patch(`/branches/${editingBranch.id}`, values);
                message.success('Branch updated');
            } else {
                await api.post('/branches', values);
                message.success('Branch created');
            }
            setIsModalOpen(false);
            setEditingBranch(null);
            form.resetFields();
            fetchBranches();
        } catch (e: any) {
            message.error(e.response?.data?.message || 'Operation failed');
        }
    };

    const handleDelete = async (id: number) => {
        try {
            await api.delete(`/branches/${id}`);
            message.success('Branch deleted');
            fetchBranches();
        } catch (e) {
            message.error('Failed to delete');
        }
    };

    const columns = [
        {
            title: 'No.',
            key: 'index',
            width: 70,
            render: (_: any, __: any, index: number) => index + 1
        },
        {
            title: 'Code',
            dataIndex: 'code',
            key: 'code',
            render: (text: string, record: any) => (
                <Space>
                    <span className="font-mono font-bold">{text}</span>
                    {record.isHQ && <Tag color="red">HQ</Tag>}
                </Space>
            )
        },
        { title: 'Name', dataIndex: 'name', key: 'name' },
        { title: 'Address', dataIndex: 'address', key: 'address' },
        {
            title: 'Staff Count',
            dataIndex: ['_count', 'users'],
            key: 'usersCount',
            render: (count: number) => count || 0
        },
        {
            title: 'Actions',
            key: 'actions',
            render: (_: any, record: any) => (
                <Space>
                    <Button
                        size="small"
                        icon={<EditOutlined />}
                        onClick={() => {
                            setEditingBranch(record);
                            form.setFieldsValue(record);
                            setIsModalOpen(true);
                        }}
                    />
                    <Popconfirm title="Delete branch?" description="This action cannot be undone." onConfirm={() => handleDelete(record.id)}>
                        <Button danger icon={<DeleteOutlined />} size="small" />
                    </Popconfirm>
                </Space>
            )
        }
    ];

    return (
        <div>
            <div className="flex justify-between items-center mb-6">
                <div>
                    <h2 className="text-2xl font-bold">Branch Management</h2>
                    <p className="text-gray-500">Manage all store locations</p>
                </div>
                <Button
                    type="primary"
                    icon={<PlusOutlined />}
                    onClick={() => {
                        setEditingBranch(null);
                        form.resetFields();
                        setIsModalOpen(true);
                    }}
                    className="bg-black text-white hover:bg-gray-800"
                >
                    Add Branch
                </Button>
            </div>

            <Table
                dataSource={branches}
                columns={columns}
                rowKey="id"
                loading={loading}
                pagination={{ pageSize: 10 }}
            />

            <Modal
                title={editingBranch ? "Edit Branch" : "Add New Branch"}
                open={isModalOpen}
                onCancel={() => {
                    setIsModalOpen(false);
                    setEditingBranch(null);
                }}
                footer={null}
            >
                <Form form={form} onFinish={handleSubmit} layout="vertical">
                    <Form.Item
                        name="name"
                        label="Branch Name"
                        rules={[{ required: true, message: 'Example: Johor Bahru Central' }]}
                    >
                        <Input placeholder="Enter branch name" prefix={<ShopOutlined />} />
                    </Form.Item>
                    <Form.Item
                        name="code"
                        label="Branch Code"
                        rules={[{ required: true, message: 'Example: JB01' }]}
                    >
                        <Input placeholder="Enter unique code (e.g. JB01)" className="font-mono" />
                    </Form.Item>
                    <Form.Item name="address" label="Address">
                        <Input.TextArea rows={2} placeholder="Full address" />
                    </Form.Item>
                    <Form.Item name="isHQ" valuePropName="checked">
                        <Checkbox>Mark as Headquarters (HQ)</Checkbox>
                    </Form.Item>
                    <Button type="primary" htmlType="submit" block className="bg-black">
                        {editingBranch ? 'Update Branch' : 'Create Branch'}
                    </Button>
                </Form>
            </Modal>
        </div>
    );
}

"use client";

import React, { useState, useEffect } from 'react';
import { Layout, Menu, theme, App as AntdApp } from 'antd';
import {
    DashboardOutlined,
    ShopOutlined,
    UserOutlined,
    LogoutOutlined
} from '@ant-design/icons';
import { useRouter, usePathname } from 'next/navigation';
import { User } from '@/lib/types';

const { Header, Sider, Content } = Layout;

export default function PortalLayout({ children }: { children: React.ReactNode }) {
    const [collapsed, setCollapsed] = useState(false);
    const [user, setUser] = useState<User | null>(null);
    const router = useRouter();
    const pathname = usePathname();
    const {
        token: { colorBgContainer, borderRadiusLG },
    } = theme.useToken();

    useEffect(() => {
        if (pathname === '/portal/login' || pathname === '/portal') return;

        const storedUser = localStorage.getItem('user');
        if (storedUser) {
            const parsed = JSON.parse(storedUser);
            if (parsed.role !== 'SUPER_ADMIN') {
                router.push('/dashboard');
            }
            // eslint-disable-next-line react-hooks/exhaustive-deps
            setUser(parsed);
        } else {
            router.push('/portal/login');
        }
    }, [router, pathname]);

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        router.push('/portal/login');
    };

    // Only show sidebar/header for authenticated Super Admins
    const isLoginPage = pathname === '/portal/login' || pathname === '/portal';

    if (!isLoginPage && !user) return null;

    if (isLoginPage) return <AntdApp>{children}</AntdApp>;

    return (
        <AntdApp>
            <Layout style={{ minHeight: '100vh' }}>
                <Sider trigger={null} collapsible collapsed={collapsed} onCollapse={setCollapsed} theme="dark" className="bg-gray-900">
                    <div className="p-4 text-white font-bold text-center text-lg border-b border-gray-800 mb-4">
                        {collapsed ? 'ADM' : 'ADMIN PORTAL'}
                    </div>
                    <Menu
                        theme="dark"
                        mode="inline"
                        selectedKeys={[pathname]}
                        className="bg-gray-900"
                        onClick={({ key }) => {
                            if (key === 'logout') handleLogout();
                            else router.push(key);
                        }}
                        items={[
                            {
                                key: '/portal/dashboard',
                                icon: <DashboardOutlined />,
                                label: 'Dashboard',
                            },
                            {
                                key: '/portal/branches',
                                icon: <ShopOutlined />,
                                label: 'Branch Management',
                            },
                            {
                                key: '/portal/users',
                                icon: <UserOutlined />,
                                label: 'User Management',
                            },
                            {
                                key: 'logout',
                                icon: <LogoutOutlined />,
                                label: 'Logout',
                                danger: true,
                            }
                        ]}
                    />
                </Sider>
                <Layout>
                    <Header style={{ padding: 0, background: colorBgContainer, display: 'flex', alignItems: 'center', justifyContent: 'space-between', paddingRight: 24, paddingLeft: 24 }}>
                        <div style={{ display: 'flex', alignItems: 'center' }}>
                            {user && (
                                <div style={{ fontWeight: '600', color: '#555', fontSize: '14px' }}>
                                    Current ID: <span style={{ color: '#E60000' }}>{user.fullName}</span>
                                    <span style={{ marginLeft: 8, padding: '2px 8px', background: '#FFF1F0', border: '1px solid #FFA39E', borderRadius: '4px', fontSize: '12px', color: '#CF1322' }}>
                                        {user.username}
                                    </span>
                                </div>
                            )}
                        </div>
                    </Header>
                    <Content
                        style={{
                            margin: '24px 16px',
                            padding: 24,
                            minHeight: 280,
                            background: colorBgContainer,
                            borderRadius: borderRadiusLG,
                        }}
                    >
                        {children}
                    </Content>
                </Layout>
            </Layout>
        </AntdApp>
    );
}

