"use client";

import React, { useState, useEffect } from 'react';
import { Form, Input, Button, Card, Typography, App } from 'antd';
import { LockOutlined, UserOutlined, ArrowRightOutlined } from '@ant-design/icons';
import { useRouter } from 'next/navigation';
import api from '@/lib/axios';
import Image from 'next/image';

const { Text } = Typography;

function LoginForm() {
    const [loading, setLoading] = useState(false);
    const router = useRouter();
    const { message } = App.useApp();

    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const onFinish = async (values: any) => {
        setLoading(true);
        try {
            const { data } = await api.post('/auth/login', values);
            if (data.user.role !== 'SUPER_ADMIN') {
                message.error('Access Denied: Super Admin credentials required');
                return;
            }
            localStorage.setItem('token', data.access_token);
            localStorage.setItem('user', JSON.stringify(data.user));
            message.success('Authorized Access: Welcome, Administrator');
            router.push('/portal/dashboard');
        } catch (err: unknown) {
            // eslint-disable-next-line @typescript-eslint/no-explicit-any
            message.error((err as any).response?.data?.message || 'Authorization failed. Access restricted.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <Form
            layout="vertical"
            onFinish={onFinish}
            size="large"
            requiredMark={false}
        >
            <Form.Item name="username" rules={[{ required: true, message: 'Admin username required' }]}>
                <Input
                    prefix={<UserOutlined className="text-gray-400 mr-2" />}
                    placeholder="Admin Username"
                    className="rounded-xl border-gray-100 hover:border-red-400 focus:border-red-500 h-12"
                />
            </Form.Item>
            <Form.Item name="password" rules={[{ required: true, message: 'Security password required' }]} className="mb-8">
                <Input.Password
                    prefix={<LockOutlined className="text-gray-400 mr-2" />}
                    placeholder="Password"
                    className="rounded-xl border-gray-100 hover:border-red-400 focus:border-red-500 h-12"
                />
            </Form.Item>
            <Form.Item className="mb-0">
                <Button
                    type="primary"
                    htmlType="submit"
                    block
                    loading={loading}
                    icon={<ArrowRightOutlined />}
                    className="h-14 rounded-xl bg-[#E60000] hover:bg-[#c40000] border-none text-lg font-bold shadow-xl shadow-red-100 transition-all transform hover:scale-[1.01]"
                >
                    Authorize Entry
                </Button>
            </Form.Item>
        </Form>
    );
}

export default function PortalLogin() {
    const [hasMounted, setHasMounted] = useState(false);

    useEffect(() => {
        setHasMounted(true);
    }, []);

    if (!hasMounted) {
        return <div className="min-h-screen bg-[#f8f9fa]" />;
    }

    return (
        <div className="min-h-screen flex items-center justify-center bg-[#f8f9fa] relative overflow-hidden" suppressHydrationWarning>
            {/* Professional Background Accents */}
            <div className="absolute top-0 left-0 w-full h-1 bg-[#E60000]" />
            <div className="absolute top-[-20%] right-[-10%] w-[50%] h-[50%] bg-red-50 rounded-full blur-[150px] opacity-70" />
            <div className="absolute bottom-[-20%] left-[-10%] w-[50%] h-[50%] bg-gray-100 rounded-full blur-[150px] opacity-50" />

            <App>
                <Card
                    className="w-full max-w-[440px] shadow-[0_30px_60px_rgba(0,0,0,0.08)] border-none rounded-3xl overflow-hidden"
                    styles={{ body: { padding: '48px 40px' } }}
                >
                    <div className="text-center mb-10">
                        <div className="inline-flex items-center justify-center mb-8">
                            <Image src="/logo.png" alt="SPR Logo" width={256} height={60} className="w-64 h-auto drop-shadow-xl" />
                        </div>
                        <div className="flex items-center justify-center">
                            <span className="h-[1px] w-4 bg-gray-200" />
                            <Text className="text-gray-400 font-semibold px-3 uppercase tracking-widest text-[10px]">Security Clearance Required</Text>
                            <span className="h-[1px] w-4 bg-gray-200" />
                        </div>
                    </div>

                    <LoginForm />

                    <div className="mt-10 pt-8 border-t border-gray-50 text-center">
                        <Text className="text-gray-300 text-[10px] tracking-widest uppercase">
                            Secure Personnel Access Only
                        </Text>
                    </div>
                </Card>
            </App>
        </div>
    );
}

"use client";

import React, { useEffect, useState } from 'react';
import { Table, Button, Modal, Form, Input, Select, Tag, Space, Popconfirm, App } from 'antd';
import { PlusOutlined, DeleteOutlined, EditOutlined, UserAddOutlined } from '@ant-design/icons';
import api from '@/lib/axios';

export default function PortalUsersPage() {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(false);
    const [isModalOpen, setIsModalOpen] = useState(false);
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const [editingUser, setEditingUser] = useState<any>(null);
    const [form] = Form.useForm();
    const { message } = App.useApp();

    // Fetch Branches for dropdown
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const [branches, setBranches] = useState<any[]>([]);
    const [searchText, setSearchText] = useState('');

    useEffect(() => {
        fetchUsers();
        fetchBranches();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const fetchUsers = async () => {
        setLoading(true);
        try {
            const { data } = await api.get('/users');
            setUsers(data);
        } catch {
            message.error('Failed to load users');
        } finally {
            setLoading(false);
        }
    };

    const fetchBranches = async () => {
        try {
            const { data } = await api.get('/branches');
            setBranches(data);
        } catch {
            console.error('Failed to load branches');
        }
    };

    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const handleSubmit = async (values: any) => {
        // Convert branchId to int if present
        if (values.branchId) values.branchId = parseInt(values.branchId);

        try {
            if (editingUser) {
                await api.patch(`/users/${editingUser.id}`, values);
                message.success('User updated');
            } else {
                await api.post('/users', values);
                message.success('User created');
            }
            setIsModalOpen(false);
            form.resetFields();
            setEditingUser(null);
            fetchUsers();
        } catch (e: unknown) {
            // eslint-disable-next-line @typescript-eslint/no-explicit-any
            message.error((e as any).response?.data?.message || 'Operation failed');
        }
    };

    const handleDelete = async (id: number) => {
        try {
            await api.delete(`/users/${id}`);
            message.success('User deleted');
            fetchUsers();
        } catch {
            message.error('Failed to delete');
        }
    };

    const columns = [
        {
            title: 'No.',
            key: 'index',
            width: 60,
            // eslint-disable-next-line @typescript-eslint/no-explicit-any
            render: (_: any, __: any, index: number) => index + 1
        },
        { title: 'Username', dataIndex: 'username', key: 'username', render: (text: string) => <span className="font-semibold">{text}</span> },
        { title: 'Full Name', dataIndex: 'fullName', key: 'fullName' },
        {
            title: 'Role',
            dataIndex: 'role',
            key: 'role',
            render: (role: string) => {
                let color = 'blue';
                if (role === 'SUPER_ADMIN') color = 'gold';
                if (role === 'TECHNICIAN') color = 'cyan';
                return <Tag color={color}>{role}</Tag>;
            }
        },
        {
            title: 'Branch',
            dataIndex: 'branch',
            key: 'branch',
            render: (branch: any) => branch ? <Tag>{branch.code}</Tag> : <span className="text-gray-400">-</span>
        },
        {
            title: 'Actions',
            key: 'actions',
            // eslint-disable-next-line @typescript-eslint/no-explicit-any
            render: (_: any, record: any) => (
                <Space>
                    <Button
                        size="small"
                        icon={<EditOutlined />}
                        onClick={() => {
                            setEditingUser(record);
                            form.setFieldsValue(record);
                            setIsModalOpen(true);
                        }}
                    />
                    <Popconfirm title="Delete user?" onConfirm={() => handleDelete(record.id)}>
                        <Button size="small" danger icon={<DeleteOutlined />} />
                    </Popconfirm>
                </Space>
            )
        }
    ];
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const filteredUsers = users.filter((u: any) =>
        u.username.toLowerCase().includes(searchText.toLowerCase()) ||
        u.fullName.toLowerCase().includes(searchText.toLowerCase())
    );

    return (
        <div>
            <div className="flex justify-between items-center mb-6">
                <div>
                    <h2 className="text-2xl font-bold">User Management</h2>
                    <p className="text-gray-500">Manage system access and roles</p>
                </div>
                <Space>
                    <Input.Search
                        placeholder="Search username or name..."
                        allowClear
                        onSearch={setSearchText}
                        onChange={(e) => setSearchText(e.target.value)}
                        style={{ width: 300 }}
                    />
                    <Button
                        type="primary"
                        icon={<UserAddOutlined />}
                        onClick={() => {
                            setEditingUser(null);
                            form.resetFields();
                            setIsModalOpen(true);
                        }}
                        className="bg-black"
                    >
                        Add User
                    </Button>
                </Space>
            </div>

            <Table dataSource={filteredUsers} columns={columns} rowKey="id" loading={loading} />

            <Modal
                title={editingUser ? "Edit User" : "Create User"}
                open={isModalOpen}
                onCancel={() => setIsModalOpen(false)}
                footer={null}
            >
                <Form form={form} onFinish={handleSubmit} layout="vertical">
                    <Form.Item name="username" label="Username" rules={[{ required: true }]}>
                        <Input readOnly={!!editingUser} disabled={!!editingUser} />
                    </Form.Item>

                    <Form.Item
                        name="password"
                        label={editingUser ? "New Password" : "Password"}
                        rules={[{ required: !editingUser }]}
                        help={editingUser ? "Leave blank to keep current password" : ""}
                    >
                        <Input.Password placeholder={editingUser ? "********" : ""} />
                    </Form.Item>

                    <Form.Item name="fullName" label="Full Name" rules={[{ required: true }]}>
                        <Input />
                    </Form.Item>

                    <div className="grid grid-cols-2 gap-4">
                        <Form.Item name="role" label="Role" rules={[{ required: true }]}>
                            <Select placeholder="Select role">
                                <Select.Option value="SUPER_ADMIN">Super Admin</Select.Option>
                                <Select.Option value="HQ_ADMIN">HQ Admin</Select.Option>
                                <Select.Option value="BRANCH_ADMIN">Branch Admin</Select.Option>
                                <Select.Option value="TECHNICIAN">Technician</Select.Option>
                                <Select.Option value="COURIER">Courier</Select.Option>
                            </Select>
                        </Form.Item>

                        <Form.Item name="branchId" label="Branch">
                            <Select placeholder="Select branch" allowClear>
                                {branches.map((b: any) => (
                                    <Select.Option key={b.id} value={b.id}>
                                        {b.code} - {b.name}
                                    </Select.Option>
                                ))}
                            </Select>
                        </Form.Item>
                    </div>

                    <Button type="primary" htmlType="submit" block className="bg-black mt-2">
                        {editingUser ? 'Update User' : 'Create User'}
                    </Button>
                </Form>
            </Modal>
        </div>
    );
}

"use client";

import React, { useEffect, useState } from 'react';
import { Card, Descriptions, Tag, Timeline, Button, Modal, Form, Select, Input, App, Spin, Space, Popconfirm } from 'antd';
import { ClockCircleOutlined, CheckCircleOutlined } from '@ant-design/icons';
import api from '@/lib/axios';
import { useAuth } from '@/hooks/useAuth';
import { formatDate } from '@/lib/utils';

interface RepairDetailModalProps {
    orderId: number | null;
    open: boolean;
    onCancel: () => void;
    onActionSuccess: () => void;
}

export default function RepairDetailModal({ orderId, open, onCancel, onActionSuccess }: RepairDetailModalProps) {
    const { user, hasRole } = useAuth();
    const { message } = App.useApp();
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const [order, setOrder] = useState<any>(null);
    const [loading, setLoading] = useState(false);
    const [actionLoading, setActionLoading] = useState(false);
    const [isAssignModalOpen, setIsAssignModalOpen] = useState(false);
    const [isFailModalOpen, setIsFailModalOpen] = useState(false);
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const [technicians, setTechnicians] = useState<any[]>([]);

    const fetchOrder = React.useCallback(async () => {
        setLoading(true);
        try {
            const { data } = await api.get(`/repairs/${orderId}`);
            setOrder(data);
        } catch {
            message.error('Failed to load order');
        } finally {
            setLoading(false);
        }
    }, [orderId, message]);
    useEffect(() => {
        if (open && orderId) {
            fetchOrder();
        } else {
            setOrder(null);
        }
    }, [open, orderId, fetchOrder]);

    const loadTechnicians = async () => {
        try {
            const { data } = await api.get('/users');
            // eslint-disable-next-line @typescript-eslint/no-explicit-any
            setTechnicians(data.filter((u: any) => u.role === 'TECHNICIAN'));
        } catch (e) {
            console.error(e);
        }
    };

    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const handleAction = async (action: string, payload: any = {}) => {
        setActionLoading(true);
        try {
            if (action === 'assign') {
                await api.patch(`/repairs/${orderId}/assign`, payload);
                setIsAssignModalOpen(false);
            } else if (action === 'start') {
                await api.patch(`/repairs/${orderId}/start`);
            } else if (action === 'complete') {
                await api.patch(`/repairs/${orderId}/complete`);
            } else if (action === 'fail') {
                await api.patch(`/repairs/${orderId}/fail`, payload);
                setIsFailModalOpen(false);
            }
            message.success('Action successful');
            fetchOrder();
            onActionSuccess();
        } catch (e: unknown) {
            // eslint-disable-next-line @typescript-eslint/no-explicit-any
            message.error((e as any).response?.data?.message || 'Action failed');
        } finally {
            setActionLoading(false);
        }
    };

    return (
        <Modal
            title={<span className="text-xl font-bold">Repair Details #{order?.orderNo}</span>}
            open={open}
            onCancel={onCancel}
            footer={null}
            width={850}
            destroyOnHidden
        >
            {loading ? (
                <div className="py-20 text-center"><Spin size="large" /></div>
            ) : order ? (
                <div className="mt-6">
                    <div className="flex justify-end mb-6">
                        <Space>
                            {hasRole(['HQ_ADMIN', 'SUPER_ADMIN']) && order.status === 'AT_HQ' && (
                                <Button type="primary" size="large" style={{ backgroundColor: '#E60000' }} onClick={() => { setIsAssignModalOpen(true); loadTechnicians(); }}>
                                    Assign Technician
                                </Button>
                            )}

                            {hasRole(['TECHNICIAN']) && order.status === 'ASSIGNED_TO_TECHNICIAN' && user?.id === order.technicianId && (
                                <Button type="primary" size="large" style={{ backgroundColor: '#E60000' }} onClick={() => handleAction('start')}>
                                    Start Repair
                                </Button>
                            )}

                            {hasRole(['TECHNICIAN']) && order.status === 'UNDER_REPAIR' && user?.id === order.technicianId && (
                                <>
                                    <Popconfirm title="Mark as Fixed?" onConfirm={() => handleAction('complete')}>
                                        <Button type="primary" size="large" style={{ backgroundColor: 'green' }}>Complete Repair</Button>
                                    </Popconfirm>
                                    <Button danger size="large" onClick={() => setIsFailModalOpen(true)}>Mark Failed</Button>
                                </>
                            )}
                        </Space>
                    </div>

                    <div className="grid grid-cols-1 gap-8">
                        <div>
                            <Card title="Device Information" variant="borderless" className="bg-gray-50 rounded-xl">
                                <Descriptions column={2} styles={{ label: { fontWeight: 'bold' } }}>
                                    <Descriptions.Item label="Device Model">{order.deviceModel}</Descriptions.Item>
                                    <Descriptions.Item label="IMEI / SN">{order.isbn_imei || '-'}</Descriptions.Item>
                                    <Descriptions.Item label="Issue Description" span={2}>{order.problemDesc}</Descriptions.Item>
                                    <Descriptions.Item label="Internal Status">
                                        <Tag color="red" variant="filled" className="font-bold">{order.status}</Tag>
                                    </Descriptions.Item>
                                    <Descriptions.Item label="Technician">{order.technician?.fullName || 'Not Assigned'}</Descriptions.Item>
                                </Descriptions>
                            </Card>
                        </div>
                        <div>
                            <Card title="Flow History" variant="borderless" className="bg-gray-50 rounded-xl">
                                <div className="px-2 py-2">
                                    <Timeline
                                        items={order.history?.map((log: any) => ({
                                            color: log.toStatus === 'REPAIR_FAILED' ? 'red' : 'blue',
                                            icon: log.isScanAction ? <CheckCircleOutlined /> : <ClockCircleOutlined />,
                                            children: (
                                                <div className="flex flex-col md:flex-row md:items-start gap-4 -mt-1 pb-4">
                                                    <div className="min-w-[150px] pt-0.5">
                                                        <span className="text-gray-400 font-bold text-sm block">{formatDate(log.createdAt)}</span>
                                                    </div>
                                                    <div className="flex-1">
                                                        <p className="font-bold text-base m-0 text-gray-800 leading-tight">
                                                            {log.toStatus.replace(/_/g, ' ')}
                                                        </p>
                                                        <p className="text-gray-500 text-sm m-0 mt-1">
                                                            {log.remark || 'Status updated'} by <span className="text-gray-700 font-semibold">{log.user?.fullName}</span>
                                                        </p>
                                                    </div>
                                                </div>
                                            )
                                        }))}
                                    />
                                </div>
                            </Card>
                        </div>
                    </div>

                    {/* Sub Modals */}
                    <Modal title="Assign Technician" open={isAssignModalOpen} onCancel={() => setIsAssignModalOpen(false)} footer={null} destroyOnHidden>
                        <Form onFinish={(vals) => handleAction('assign', vals)} layout="vertical" className="mt-4">
                            <Form.Item name="technicianId" label="Select Technician" rules={[{ required: true }]}>
                                <Select placeholder="Select a tech..." size="large">
                                    {technicians.map(t => (
                                        <Select.Option key={t.id} value={t.id}>{t.fullName} ({t.username})</Select.Option>
                                    ))}
                                </Select>
                            </Form.Item>
                            <Button type="primary" htmlType="submit" loading={actionLoading} block size="large" style={{ backgroundColor: '#E60000' }}>Confirm Assignment</Button>
                        </Form>
                    </Modal>

                    <Modal title="Report Repair Failure" open={isFailModalOpen} onCancel={() => setIsFailModalOpen(false)} footer={null} destroyOnHidden>
                        <Form onFinish={(vals) => handleAction('fail', vals)} layout="vertical" className="mt-4">
                            <Form.Item name="reason" label="Failure Reason" rules={[{ required: true }]}>
                                <Input.TextArea rows={4} placeholder="Why this device cannot be fixed?" size="large" />
                            </Form.Item>
                            <Button danger type="primary" htmlType="submit" loading={actionLoading} block size="large">Confirm Failure</Button>
                        </Form>
                    </Modal>
                </div>
            ) : null}
        </Modal>
    );
}

import { useState, useEffect } from 'react';
import { User } from '@/lib/types';

export function useAuth() {
    const [user, setUser] = useState<User | null>(null);

    useEffect(() => {
        const stored = localStorage.getItem('user');
        if (stored) {
            // eslint-disable-next-line react-hooks/exhaustive-deps
            setUser(JSON.parse(stored));
        }
    }, []);

    const hasRole = (roles: string[]) => {
        return user && roles.includes(user.role);
    };

    return { user, hasRole, loading: !user && typeof window !== 'undefined' && localStorage.getItem('user') };
}
